package org.exmple.Assignment4;

public class WidningOfIntToAll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 12;
		
		String str = Integer.toString(a);
		System.out.println("intger to string : "+str);
		
		
		double myDouble = (double)a;
		System.out.println("integer to double : "+myDouble);
		
		float myFloat = (float)a;
		System.out.println("integer to float : "+myFloat);
		

		
	}

}
